import { Component } from "./Component";
import { Element, GenericElement, OverrideProps } from "./Element";
import { RobloxComponent, RobloxElement } from "./RobloxComponents";

export namespace React {
	// create element from registered component name
	function createElement<props, baseInstance extends Instance|undefined>(
		name:string,
		overrideProps:OverrideProps<props,baseInstance>,
		...children:GenericElement[]
	):Element<props,baseInstance>;
	// create element from component directly
	function createElement<props, baseInstance extends Instance|undefined>(
		comp:Component<props,baseInstance>,
		overrideProps:OverrideProps<props,baseInstance>,
		...children:GenericElement[]
	):Element<props,baseInstance>;
	// create roblox element from classname lower case
	function createElement<T extends keyof JSX.IntrinsicElements>(
		name:T,
		props: JSX.IntrinsicElements[T],
		...children:GenericElement[]
	):RobloxElement<JSX.IntrinsicElementsToClass[T]>;
	// create roblox element from component
	function createElement<T extends Instance>(
		comp:RobloxComponent<T>,
		props: WritableInstanceProperties<T>,
		...children:GenericElement[]
	):void;
}

